import LineGradient from "../components/LineGradient";

const Project = ({ title, subtitle, link }) => {

  const overlayStyles = `absolute h-36 w-96 opacity-0 hover:opacity-90 transition duration-500
    bg-grey z-30 flex flex-col justify-center items-center text-center p-16 text-deep-blue scale-50`;
  const projectTitle = title.split(" ").join("-").toLowerCase();

  return (

     <div>
       {/* <div className={overlayStyles} >
        <p className="text-2xl font-playfair">{title}</p>
        <p className="mt-7">
          {subtitle}
          <br />
          <br />
          <a href={link} target="_blank">
            Link To Project
          </a>
        </p>
      </div> */}
      <img src={require(`../assets/${projectTitle}.jpeg`)} alt={projectTitle} className=" h-72 w-52" />
     </div>
  );
};

const Projects = () => {
  return (
    <section id="projects" className="pt-24 pb-24 flex flex-col justify-center items-center">

        <div>
          <p className="font-playfair font-semibold text-4xl w-">
            <span className="text-red">PRO</span>JECTS
          </p>
          <div className="flex justify-center mt-5">
            <LineGradient width="w-full" />
          </div>
        </div>
        <div className="mt-10 mb-10 w-[34rem] flex justify-center items-center">
          <p>Various Projects from small scale to big scale including technologies
          like HTML, CSS, JavaScript, TailWindCSS, ReactJS, NodeJs , ExpressJs , PostgreSQL, C,
          C++, GitHub etc.</p>
        </div>

      <div className="flex justify-evenly">

          <Project
            title="Project 1"
            subtitle="College's Technical Club Website Created Using HTML, CSS, JavaScript etc"
            link="https://amanhex.github.io/CestaWebsite/"
          />
          <Project
            title="Project 2"
            subtitle="Portfolio Created Using HTML, CSS, ReactJS, TailWind CSS, Framer-Motion"
            link="https://amanhex.github.io/react-portfolio/"
          />
          <Project
            title="Project 3"
            subtitle="Car Parking Management System developed using C++"
            link="https://github.com/amanhex/Car-Parking-Management-System/"
          />

      </div>
    </section>
  );
};

export default Projects;
